package CoreJava;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//1 to 10
		
		int i=0;
		int j=10;
		int z=20;
		while(i<10)
		{
			System.out.println(i);
			i++;
		}

		while(j>0) {
			System.out.println(j);
			j--;
	}
		
		do {
			System.out.println(z);
			z++;
			
		}while(z>30);
		
		
		
		
	}
	

}
